package Assignments;

public class If_else_block {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

			int a=6;
			if(a<=3)
			{
				System.out.println("Number is: ");
				System.out.println(a);
			}
			else
			{
				System.out.println("Number is: ");
				System.out.println(a);
			}

		}
}
