package Assignments;

public class LogicalOperatorsAss_no_9 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		boolean a = true;
		boolean b = false;
		
		System.out.println("a : "+a);
		System.out.println("b : "+b);
		System.out.println("a && b : "+ (a && b));
		System.out.println("a || b : "+ (a || b));
		System.out.println("!a : "+ (!a));
		System.out.println("!b : "+ (!b));
		System.out.println("!(a && b): "+ (!(a && b)));
		System.out.println("!(a || b :"+ (!(a ||b)));

	}

}
